INSERT INTO `eca_maintb` (`id`, `title`, `filter`, `tbpic`, `keywords`, `info`, `author`, `source`, `hits`, `category`, `channel`, `addtime`, `updatetime`, `top`, `purview`, `is_html`, `verify`, `url`, `lang`, `is_url`, `url_add`, `title_color`, `title_style`, `is_open`, `cache_time`, `custom_url`, `c_order`, `content_key`, `small_title`) VALUES
(1, '企业文化介绍', NULL, '', '', '	企业文化介绍\r\n\r\n	 \r\n\r\n	企业文化介绍企业文化介绍企业文化介绍企业文化介绍企业文化介绍\r\n', '未知', '未知', 6, 7, 1, '1410134971', '1411466949', 0, 0, 0, 0, NULL, 'cn', 0, NULL, NULL, 0, 0, '30', NULL, NULL, NULL, NULL),
(2, '联系我们介绍', NULL, '', '', '	联系我们介绍\r\n\r\n	 \r\n\r\n	联系我们介绍联系我们介绍联系我们介绍联系我们介绍\r\n', '未知', '未知', 3, 8, 1, '1410134992', '1412770243', 0, 0, 0, 0, NULL, 'cn', 0, NULL, NULL, 0, 0, '30', NULL, NULL, NULL, NULL),
(3, '行业背景介绍', NULL, '', '', '	行业背景介绍\r\n\r\n	 \r\n\r\n	行业背景介绍行业背景介绍行业背景介绍\r\n', '未知', '未知', 2, 6, 1, '1410134965', '1411466971', 0, 0, 0, 0, NULL, 'cn', 0, NULL, NULL, 0, 0, '30', NULL, NULL, NULL, NULL),
(4, '公司介绍', NULL, '', '', '	公司介绍公司介绍\r\n', '未知', '未知', 10, 5, 1, '1410134993', '1411466943', 0, 0, 0, 0, NULL, 'cn', 0, NULL, NULL, 0, 0, '30', NULL, NULL, NULL, NULL),
(5, '六大机构看好非农 白银仍有一跌', '', '', '', '\r\n	行情回顾\r\n	\r\n	昨日白银的走势如同笔者所预判的一样，盘整了这么久，暴跌终于回归，自欧盘前后银价迅速跳水，日内从高点19.51美元迅速下挫，美盘时段一度达到19.09美元这一近三个...', '未知', '未知', 3, 10, 2, '1410138571', '1410138571', 0, 0, 0, 0, 'htm/day/2014_0908_5.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '六大机构看好非农 白银仍有一跌'),
(6, '白银9月已“预订”反弹 涨势或维持至年底', '', 'img/20140908/20140908091655_thumb.png', '', '\r\n	&nbsp;尽管今年7月和8月国际银价分别录得9.8%和4.4%的巨大跌幅，且白银ETF也跟随走低，但相信进入9月后形势将出现反转。事实上，最近一周银价已止住之前的六周连阴，暗示8月的下行...', '未知', '未知', 3, 10, 2, '1410138550', '1410138550', 0, 0, 0, 0, 'htm/day/2014_0908_6.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '白银9月已“预订”反弹 涨势或维持至年底'),
(7, '中印需求出现转机 引爆白银九月行情', '', '', '', '\r\n	上周，国际现货黄金价格下探1273美元/盎司附近后强劲反弹，但仍受制于1300美元/盎司整数关口下方压制。虽然从消息面上，美国宏观因素表现强劲，但是在地缘政治和需求改善及技术...', '未知', '未知', 15, 11, 2, '1410138599', '1410138599', 0, 0, 0, 0, 'htm/week/2014_0908_7.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '中印需求出现转机 引爆白银九月行情'),
(8, '美指高位岿然不动 白银多头动能不足', '', '', '', '\r\n	行情回顾\r\n	\r\n	昨天自开盘后银价一路不断上涨，欧盘前后更是刷新了半个月以来的新高至19.90美元一线，然而，晚间美国方面强劲的经济数据沉重打压了白银的涨势，空头迅速的收回了...', '未知', '未知', 2, 11, 2, '1410138546', '1410138546', 0, 0, 0, 0, 'htm/week/2014_0908_8.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '美指高位岿然不动 白银多头动能不足'),
(9, '地缘危机唱主角 白银反弹会持续', '', '', '', '\r\n	消息解读：\r\n\r\n	　　昨日在经济数据方面较为清淡，而地缘政治紧张状态却依然持续。在巴以方面，自双方达成长期停火协议之后，紧张的局势也有所缓和。不过，尽管已停火，以色列...', '未知', '未知', 3, 11, 2, '1410138575', '1410138575', 0, 0, 0, 0, 'htm/week/2014_0908_9.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '地缘危机唱主角 白银反弹会持续'),
(10, '投资者预期中国实施刺激 LME铜收于三周高点', '', '', '', '\r\n	　　铜期货周一微幅走高，对于中国可能推出刺激举措的期望抵消了弱于预期的美国住房销售数据带来的影响。\r\n\r\n	　　纽约商品交易所Comex分部九月铜期货涨1.35美分，至3.2180美元/磅...', '未知', '未知', 2, 12, 2, '1410138596', '1410138596', 0, 0, 0, 0, 'htm/Financial/2014_0908_10.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '投资者预期中国实施刺激 LME铜收于三周高点'),
(11, '周五夜盘沪铝振荡持稳', '', '', '', '\r\n	　　盘面走势，周五夜盘铝小幅下跌，沪铝主力1410下跌20元/吨至14310元/吨，跌幅为0.14%；美元指数上涨0.22%至82.331；标普指数上涨1.71%。\r\n\r\n	　　消息面，昨日中欧制造业PMI表现不佳，...', '未知', '未知', 2, 12, 2, '1410138573', '1410138573', 0, 0, 0, 0, 'htm/Financial/2014_0908_11.html', 'cn', 0, 'http://', '', 0, 0, '30', '', NULL, '', '周五夜盘沪铝振荡持稳'),
